import json

def query_string_to_json(query_string, file_name):
    # Split the string into key-value pairs
    pairs = query_string.split('&')

    # Parse the key-value pairs
    parsed_data = {}
    for pair in pairs:
        key, value = pair.split('=', 1)
        # Attempt to convert numeric values for better readability
        try:
            # Handling comma-separated numbers (like "5,6,9") and float values
            if ',' in value:
                value = [float(v) if '.' in v else int(v) for v in value.split(',')]
            elif value.isdigit():
                value = int(value)
            elif value.replace('.', '', 1).isdigit():
                value = float(value)
        except ValueError:
            pass  # Keep the original value if conversion fails
        parsed_data[key] = value

    with open(file_name, 'w') as file:
        json.dump(parsed_data, file, indent=4)

    return parsed_data

